import '../App.js';
import React from 'react';
import '../stylesheets/App.css';
import axios from 'axios';

class ProfilePage extends React.Component{
    constructor(props){
        super(props);
        this.state={
            currentPage: (this.props.userType ==="admin"? "users": "Questions"),
            currentUser: this.props.user,
            uType: this.props.userType,
            questions: null,
            answers: null
        }
        this.setPage = this.setPage.bind(this);
        this.updateUser = this.updateUser.bind(this);
    }
    async componentDidMount(){
        axios.get(`http://localhost:8000/userquest/${this.props.user._id}`).then(res=>{
            this.setState({questions: res.data})
        })
        axios.get(`http://localhost:8000/userans/${this.props.user._id}`).then(res=>{
            this.setState({answers: res.data})
        })
    }
    async updateUser(user){
        console.log(user)
        let quest = await axios.get(`http://localhost:8000/userquest/${user._id}`)
        let ans = await axios.get(`http://localhost:8000/userans/${user._id}`)
        console.log(user.admin)
        console.log(quest)
        console.log(ans)
        if (user.admin == true){
            this.setState({currentUser: user, questions:quest.data, answers:ans.data, currentPage : "Questions", uType : "admin"});
        }
        else{
            console.log("here")
            this.setState({currentUser: user, questions:quest.data, answers:ans.data, currentPage : "Questions", uType: "user"});
        }
    }
    setPage(name){
        this.setState({currentPage:name})
    }
    // userQs(){
    //     let questions = this.props.questions;
    //     console.log(questions);
    //     console.log("finding questions user asked");
    //     let userQs = [];
    //     for(let i=0; i<questions.length;i++){
    //       console.log(questions[i].asked_by.email + " " + this.props.user.email);
    //       if((questions[i].asked_by.email === this.props.user.email)){ //display all questions user asked
    //         userQs.push(questions[i]);
    //       }
    //     }
    //     console.log(userQs);
    //     return userQs;
    // }
    //   userAs(){
    //     let questions = this.props.questions;
    //     console.log(questions);
    //     console.log("finding questions user answered");
    //     let userAs = [];
    //     for(let i=0; i<questions.length;i++){
    //       for(let j=0;j<questions[i].answers.length;j++){
    //         if(questions[i].answers[j].ans_by.email === this.props.user.email){
    //           userAs.push(questions[i]);
    //         }
    //       }
    //     }
    //     return userAs;
    // }
    render(){
        if(this.state.questions==null ||this.state.answers == null ){
            return
        }
        let uinfo = "Member since " + this.props.handle(this.props.user.memberTime) +  ", Reputation: " + this.props.user.rep;
        return(
          <div id="profile">
            <h1 id="profile_intro">Profile of {this.state.currentUser.username}</h1>
            <p id="Member">{uinfo}</p>
            <NavigationBar userType = {this.state.uType} setPage={this.setPage}/>
            <InnerTable users={this.props.users} userType={this.props.userType} currPage = {this.state.currentPage} questions={this.state.questions} answers={this.state.answers} updateUser = {this.updateUser} updatePage={this.props.updatePage}
            onQuestion={this.props.onQuestion}
            handle = {this.props.handle}
            currPage = {this.props.currPage} updatePopulateForm={this.props.updatePopulateForm}/>
          </div>
        )
    }
}
class NavigationBar extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        return(
        <div id="nav">
            {this.props.userType === "admin" &&(
             <a href="#admin" id="admin_control" onClick={()=>this.props.setPage("users")}> Users</a>
            )}
            <a href="#userq" id="user_questions" onClick={()=>this.props.setPage("Questions")}>Questions</a>
            <a href="#userans" id="user_answers" onClick={()=>this.props.setPage("Answers")}>Answered Questions</a>
            <a href="#usertags" id="user_tags" onClick={()=>this.props.setPage("Answers")}>Tags</a>
        </div>
    )}
}
class InnerTable extends React.Component{
    render(){
        const rows = [];
        let users = this.props.users;
        let questions = this.props.questions;
        let answers = this.props.answers;
        if(this.props.currPage === "users"){
            users.forEach((user) => {
                console.log(user);
                rows.push(
                  <UserRow user={user} key={user._id} handleAdminState={this.props.handleAdminState} handleCurrUser={this.props.handleCurrUser} updateUserType={this.props.updateUserType}
                  updatePage = {this.props.updatePage} questions={this.props.questions} answers={this.props.answers} updateUser={this.props.updateUser}/>
                );
              });
        }
        else if (this.props.currPage === "Questions"){
            console.log("heree");
            console.log(questions.length);
            if (questions.length == 0){
                console.log("in");
                return <h2>You have not asked any questions yet</h2>
            }
            questions.forEach((q) => {
                rows.push(
                  <Question quest = {q}/>
                );
              });
        }
        else{
            if (answers.length == 0){
                return <h2>You have not answered any questions yet</h2>
            }
            answers.forEach((a) => {
                rows.push(
                  <QuestionRow question={a}
                  key={a._id}
                  updatePage={this.props.updatePage}
                  onQuestion={this.props.onQuestion}
                  handle = {this.props.handle}
                  currPage = {this.props.currPage} updatePopulateForm={this.props.updatePopulateForm}/>
                );
            });
        }
        return(
            <div id = "info">
               <table id= "user_table">
                <tbody>
                {this.props.currPage === "users" ? rows : null}
                {this.props.currPage === "Questions" ? rows : null}
                {this.props.currPage === "Answers" ? rows : null}
                </tbody>
                </table>
            </div>
        )
    }
}
