        else if (this.props.currentPage === "Questions"){
            console.log("heree");
            console.log(questions.length);
            if (questions.length == 0){
                console.log("in");
                return <h2 className='warning'>You have not asked any questions yet</h2>
            }
            questions.forEach((q) => {
                rows.push(
