import '../App.js';
import React from 'react';
import '../stylesheets/App.css';
import axios from 'axios';

class ProfilePage extends React.Component{
    constructor(props){
        super(props);
        this.state={
            currentPage: (this.props.userType ==="admin"? "users": "Questions"),
            currentUser: this.props.user,
            questions: null,
            answers: null
        }
        this.setPage = this.setPage.bind(this);
        this.updateUser = this.updateUser.bind(this);
    }
    async componentDidMount(){
        axios.get(`http://localhost:8000/userquest/${this.props.user._id}`).then(res=>{
            this.setState({questions: res.data})
        })
        axios.get(`http://localhost:8000/userans/${this.props.user._id}`).then(res=>{
            this.setState({answers: res.data})
        })
    }
    async updateUser(user){
        console.log(user)
        let quest = await axios.get(`http://localhost:8000/userquest/${user._id}`)
        let ans = await axios.get(`http://localhost:8000/userans/${user._id}`)
        this.setState({currentUser: user, questions:quest, answers:ans});
    }
    setPage(name){
        this.setState({currentPage:name})
    }
    // userQs(){
    //     let questions = this.props.questions;
    //     console.log(questions);
    //     console.log("finding questions user asked");
    //     let userQs = [];
    //     for(let i=0; i<questions.length;i++){
    //       console.log(questions[i].asked_by.email + " " + this.props.user.email);
    //       if((questions[i].asked_by.email === this.props.user.email)){ //display all questions user asked
    //         userQs.push(questions[i]);
    //       }
    //     }
    //     console.log(userQs);
    //     return userQs;
    // }
    //   userAs(){
    //     let questions = this.props.questions;
    //     console.log(questions);
    //     console.log("finding questions user answered");
    //     let userAs = [];
    //     for(let i=0; i<questions.length;i++){
    //       for(let j=0;j<questions[i].answers.length;j++){
    //         if(questions[i].answers[j].ans_by.email === this.props.user.email){
    //           userAs.push(questions[i]);
    //         }
    //       }
    //     }
    //     return userAs;
    // }
    render(){
        if(this.state.questions==null ||this.state.answers == null ){
            return
        }
        let uinfo = "Member since " + this.props.handle(this.props.user.memberTime) +  ", Reputation: " + this.props.user.rep;
        return(
          <div id="profile">
            <h1 id="profile_intro">Profile of {this.props.user.username}</h1>
            <p id="Member">{uinfo}</p>
            <NavigationBar userType = {this.props.userType} setPage={this.setPage}/>
            <InnerTable users={this.props.users} userType={this.props.userType} currPage = {this.state.currentPage} questions={this.state.questions} answers={this.state.answers} updateUser = {this.updateUser}/>
          </div>
        )
    }
}
class NavigationBar extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        return(
        <div id="nav">
            {this.props.userType === "admin" &&(
             <a href="#admin" id="admin_control" onClick={()=>this.props.setPage("users")}> Users</a>
            )}
            <a href="#userq" id="user_questions" onClick={()=>this.props.setPage("Questions")}>Questions</a>
            <a href="#userans" id="user_answers" onClick={()=>this.props.setPage("Answers")}>Answered Questions</a>
            <a href="#usertags" id="user_tags" onClick={()=>this.props.setPage("Answers")}>Tags</a>
        </div>
    )}
}
class InnerTable extends React.Component{
    render(){
        const rows = [];
        let users = this.props.users;
        let questions = this.props.questions;
        let answers = this.props.answers;
        if(this.props.currPage === "users"){
            users.forEach((user) => {
                console.log(user);
                rows.push(
                  <UserRow user={user} key={user._id} handleAdminState={this.props.handleAdminState} handleCurrUser={this.props.handleCurrUser} updateUserType={this.props.updateUserType}
                  updatePage = {this.props.updatePage} questions={this.props.questions} answers={this.props.answers} updateUser={this.props.updateUser}/>
                );
              });
        }
        else if (this.props.currPage === "Questions"){
            console.log("heree");
            console.log(questions);
            questions.forEach((q) => {
                rows.push(
                  <Question quest = {q}/>
                );
              });
        }
        else{
            answers.forEach((a) => {
                rows.push(
                  <Answers answers = {a}/>
                );
            });
        }
        return(
            <div id = "info">
               <table id= "user_table">
                <tbody>
                {this.props.currPage === "users" ? rows : null}
                {this.props.currPage === "Questions" ? rows : null}
                {this.props.currPage === "Answers" ? rows : null}
                </tbody>
                </table>
            </div>
        )
    }
}
class Question extends React.Component{
    render(){
        const question = this.props.question;
        return(
            <tr className="quest">
                <a>{this.props.quest.title}</a>
            </tr>
        )
    }
}
class Answers extends React.Component{
    render(){
        const question = this.props.question;
        return(
        <tr className="table_r1">
            <td className="summary">{question.votes}{question.votes<=1 ? " vote" : " votes"}<br></br>{question.answers.length}{question.answers.length<=1 ? " answer" : " answers"}<br></br>{question.views}{question.views<=1 ? " view" : " views"}</td>
            <td className="question"><a href= "#question" onClick={()=>this.saveQuestion(question)}>{question.title}</a><br></br>
             {this.createButtons()}
            </td>
            <td className="user_cell">
            <p className="u">{question.asked_by.username}</p>
            <p className="t">{"asked " + this.props.handle(question.ask_date_time) + " ago"}</p>
            </td>
          </tr>
        )
    }
}
class UserRow extends React.Component{
    constructor(props){
      super(props);
      this.deleteUser = this.deleteUser.bind(this);
      this.updatePopup = this.updatePopup.bind(this);
      this.state={triggerPopup:false}
    }
    goToUser(u){
    //   this.props.updatePage("profile_page");
    //   this.props.updateUserType("loggedin"); //check this logic over. In questiontable for profile page do not care about usertype(only used for differentiating between guest not differentiating between logged in and admin) since adminstate is set to true.
    //   this.props.handleAdminState(true);
    //   this.props.handleCurrUser(u);
         this.props.updateUser(u);
    }
    updatePopup(val){
      this.setState({triggerPopup:val});
    }
    getQuestionsByUser(u){
      let allquest = this.props.questions;
      console.log(allquest);
      let questsToDelete = [];
      for(let i=0;i<allquest.length;i++){
        if(allquest[i].asked_by._id === u._id){
          questsToDelete.push(allquest[i]);
          console.log("question to delete: " + allquest[i].title);
        }
      }
      return questsToDelete;
    }
    getAnswersByUser(u){
      let allAns = this.props.answers;
      console.log(allAns);
      let ansToDelete = [];
      for(let i=0;i<allAns.length;i++){
        if(allAns[i].ans_by._id === u._id){
          ansToDelete.push(allAns[i]);
          console.log("answer to delete: " + allAns[i].text);
        }
      }
      return ansToDelete;
    }
    deleteUser=async(u)=>{
      console.log("in delete user " + u.username);
      let qsToDelete = this.getQuestionsByUser(u);
      await axios.post(`http://localhost:8000/deleteUserQuestions`, qsToDelete).then(()=>{
          console.log("deleted questions and everything with it!");
          let ansToDelete = this.getAnswersByUser(u);
          axios.post(`http://localhost:8000/deleteUserAnswers`, ansToDelete).then(()=>{
            console.log("deleted all answers user made");
          }).catch((err)=>{console.log(err)})
          axios.post(`http://localhost:8000/deleteUser`, this.props.user).then(()=>{
            console.log("deleted user!");
            this.props.updateUsers();
          }).catch((err)=>{console.log(err)})
      }).catch((err)=>{console.log(err)})
    }
    render(){
      const user=this.props.user;
      console.log("in UserRow " + user);
      return(
            <tr className="table_r1">
              <td className="question"><a href= "#user" onClick={()=>this.goToUser(user)}>{user.username}</a>
              <button type="button"  id= "d_button" onClick={()=>this.updatePopup(true)}>Delete User</button>
              {this.state.triggerPopup == true && (
                <Popup deleteUser={this.deleteUser} updatePage={this.props.updatePage} user={user}/>
                )}
              </td>
            </tr>
      );
    }
}
