import '../App.js';
import React from 'react';
import '../stylesheets/App.css';
import axios from 'axios';
class AnswerTable extends React.Component {
  constructor(props){
    super(props)
    this.compareAnsDate = this.compareAnsDate.bind(this);
  }
  compareAnsDate(x, y){
    return new Date(y.ans_date_time) - new Date(x.ans_date_time);
  }
  render() {
    console.log(this.props.user);
    let question = this.props.currQuestion;
    axios.post(`http://localhost:8000/${question.url}/views`)
    .then(response => {
        console.log(response);
    })
    .catch(error => {
        console.error('Error saving question:', error);
    });
    let ans = question.answers.slice().sort(this.compareAnsDate);
    let rows=[]
    ans.forEach(answer => {
        rows.push(
          <AnswerRow
            answer={answer}
            key={answer._id}
            updatePage={this.props.updatePage}
            handle={this.props.handle}
            user={this.props.user} userType={this.props.userType} currQuestion={this.props.currQuestion} updateQuest={this.props.updateQuest}
          />
        );
    });
    return (
      <div className="answer" id="answer_page">
          <QuestionTitle q={question} updatePage={this.props.updatePage} userType={this.props.userType}/>
          <QuestionText q={question} handle = {this.props.handle} user={this.props.user} userType={this.props.userType} currQuestion={this.props.currQuestion} updateQuest={this.props.updateQuest}/> 
            {/* {this.props.userType != "guest" && (
            <QuestionVotes q={question} user={this.props.user} userType={this.props.userType} currQuestion={this.props.currQuestion} updateQuest={this.props.updateQuest}/>
            )}
            <QuestionText q={question} handle = {this.props.handle}/> */}
        <span id="answer_number">{question.answers.length}{question.answers.length<=1 ? " answer" : " answers"}</span>
        <table className="answer_posts" id="answer_posts">
        {rows}
        </table>
        <br></br>
        {this.props.userType != "guest" && (
        <button type="button" id="ans_button" onClick={() => this.props.updatePage('answer_form')}>Answer Question</button>
        )}
      </div>
    );
  }
}

class QuestionTitle extends React.Component{
  render(){
    console.log(this.props.q);
    let q = this.props.q;
    return(
      <table id="answer_top">
      <tr id="intro">
        <td id="c2">
          <h1 id="answer_title">{q.title}</h1>
          <span id="views_count">{q.views}{q.views<=1 ? " view" : " views"}</span>
        </td>
        <td id="c3">
        {this.props.userType != "guest" && (
          <button type="button" id="ab" onClick={() => this.props.updatePage('question_form')}>Ask Question</button>
        )}
        </td>
      </tr>
      </table>
    );
  }
}

class QuestionVotes extends React.Component {
  updateVotes(direction){
    let q = this.props.q;
    if(direction==="up"){
      console.log(this.props.user);
      console.log(this.props.user._id);
      axios.post(`http://localhost:8000/posts/${this.props.user._id}/${q._id}/${direction}`)
      .then(response => {
          console.log(response);
      })
      .catch(error => {
          console.error('Error saving votes:', error);
      });
      q.votes++;
      this.props.updateQuest("answer_page", q);
      console.log("increment");
      this.props.user.rep+=5;
    }
    else{
      axios.post(`http://localhost:8000/posts/${this.props.user._id}/${q._id}/${direction}`)
      .then(response => {
          console.log(response);
      })
      .catch(error => {
          console.error('Error saving votes:', error);
      });
      q.votes--;
      this.props.user.rep-=10;
    }
  }
  render(){
    let q = this.props.currQuestion;
    return(
      <div>
        <button type="button" id="vote_up" onClick={() => this.updateVotes("up")}></button>
        <p id="vote">{q.votes}</p>
        <button type="button" id="vote_down" onClick={() => this.updateVotes("down")}></button>
      </div>
    )
  }
}

class QuestionText extends React.Component {
  render() {
    let q = this.props.q;
    q.views++;
    return (
      <div>
      <table id="second_row">
      <tr id="ans_row">
        <td id="c5">
        <QuestionVotes q={q} user={this.props.user} userType={this.props.userType} currQuestion={this.props.currQuestion} updateQuest={this.props.updateQuest}/>
        </td>
        <td id="c6">
          <p id="question_text">{changeText(q.text)}</p>
        </td>
        <td id="c7">
          <p className="userasked" id="userasked">{q.asked_by}</p>
          <p className="time" id="time">{"asked " + this.props.handle(q.ask_date_time)}</p>
        </td>
      </tr>
      </table>
      </div>
    );
  }
}

class AnswerRow extends React.Component {
  render() {
    console.log(this.props.answer);
    let ans = this.props.answer;
    console.log(ans.text);
    return(
      <tbody>
        <tr className="answer_box">
          <td id="answer_vote">
          <QuestionVotes ans={ans} user={this.props.user} userType={this.props.userType} currQuestion={this.props.currQuestion} updateQuest={this.props.updateQuest}/>
          </td>
          <td className="answer_text">
            {changeText(ans.text)}
          </td>
          <td className="info_cell">
            <p className="user_info">{ans.ans_by}</p>
            <p className="time_info">{"answered "+ this.props.handle(ans.ans_date_time)}</p>
          </td>
        </tr>
      </tbody>
    );
  }
}
function changeText(text){
  let link = /\[((?:\[[^\]]*?\]|[^[\]])*?)\]\((.*?)\)/g; //[...](...)
  let check;
  let output=[];
  let start=0; //keeps track of the starting index
  while((check = link.exec(text))!==null){
    output.push(text.slice(start, check.index));
    start = link.lastIndex;
    output.push(<a key= {check[1]+ check[2]} target="_blank" rel="noreferrer" href={check[2]}>{check[1]}</a>);
  }
  //push the rest into the array
  output.push(text.slice(start));
  return output;
}
export default AnswerTable;
