import '../App.js';
import React from 'react';
import Header from './Header.js'
import MainTable from './MainTable.js'
import '../stylesheets/App.css';
import axios from 'axios';
import Login from './Login.js';
import Register from './Register.js'
import Welcome from './Welcome.js'


class MainPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {currPage: "welcome", quest: null, answers:null, highlighted: false, all_quest: null, userSession: false, userType: null, user: null}
        this.updatePage = this.updatePage.bind(this);
        this.updateQuest = this.updateQuest.bind(this);
        this.updateTags = this.updateTags.bind(this);
        this.updateAnswers = this.updateAnswers.bind(this);
        this.setCurrPage = this.setCurrPage.bind(this);
        this.updateUserType = this.updateUserType.bind(this);
        this.updateUser = this.updateUser.bind(this);
    }
    componentDidMount() {
        console.log("came into mounted");
        axios.get('http://localhost:8000/questions')
        .then(response => {
            console.log(response.data);
            this.setState({ quest: response.data.slice().sort(compareDates), all_quest: response.data.slice().sort(compareDates)});
        })
          .catch(error => {
            console.error('Error fetching questions data:', error);
        });
        axios.get('http://localhost:8000/answers')
        .then(response => {
            console.log(response.data);
            this.setState({ answers: response.data });
        })
          .catch(error => {
            console.error('Error fetching answers data:', error);
        });
    }
    setCurrPage = (name) =>{
      console.log("setting page");
      this.setState({ currPage:name});
    }
    updatePage = (name, sorted) => {
      console.log("in update page");
      if(sorted==null){
        axios.get('http://localhost:8000/questions')
        .then(response => {
            console.log(response.data);
            this.setState({ currPage:name, quest: response.data.slice().sort(compareDates), all_quest: response.data.slice().sort(compareDates)});
        })
          .catch(error => {
            console.error('Error fetching data:', error);
        });
      }
      else{
        this.setState({ currPage:name, quest: sorted});
      }
    }
    updateUserSession = () =>{
      this.setState({userSession: true});
      axios.get('http://localhost:8000/auth')
        .then(response => {
        console.log("make sure identity: " + response.data);
        }).catch(() =>{
          console.log("cant get session");
      });
    }
    updateUserType = (userType)=>{
      this.setState({userType:userType});
    }
    updateUser = (email) =>{
      console.log("in updating user");
      axios.get(`http://localhost:8000/users/${email}`).then(u =>{
          console.log("USER: " + u.data + " is authenticated");
          this.setState({user:u.data});
      }).catch(()=>{
        console.log("cant update user");  
      });
    }
    updateAnswers = () => {
      axios.get('http://localhost:8000/answers')
        .then(response => {
            console.log(response.data);
            this.setState({ answers: response.data});
        })
          .catch(error => {
            console.error('Error fetching data:', error);
        });
    }
    updateTags = () =>{
      axios.get('http://localhost:8000/tags')
        .then(response => {
            console.log("BIG STUFF");
            console.log(response.data);
            this.setState({ tags: response.data});
        })
          .catch(error => {
            console.error('Error fetching data:', error);
        });
    }
    updateQuest= (name, l)=>{
        console.log("came here");
        this.setState({currPage: name, quest: l});
        console.log(this.state.quest);
    }
    searchTag=(input, question)=>{
        let t = question.tags;
        console.log(t);
        for(let i=0; i<t.length; i++)
        {
            if(((t[i]).name).toLowerCase()===input.toLowerCase()){
                return true;
            }
        }
    }
    isTag=(inputWord, question)=>{
        if(inputWord.includes(']')){
          if(inputWord.slice(-1)===']' && inputWord.slice(0,1)==='['){
            let found = this.stripTag(inputWord, question);
            return found; //if a tag will return true or else empty
          }
          else{
            console.log("error!"); //whats the procedure for invalid inputs ex: [react or [re]act]?
            return;
          }
        }
      }
    stripTag = (inputWord, question)=>{
        let strippedTag = inputWord.replace(/\[|\]/g, '');
        let foundTag = this.searchTag(strippedTag, question);
        if(foundTag){
          return true;
        }
        else{
          return false;
        }
     }
    render(){
        if(this.state.quest == null || this.state.all_quest == null){
            return
        }
        console.log(this.state.all_quest);
        console.log(this.state.currPage);
        console.log(this.state.userType);
        return(
           <div className="trv">
            <Header currPage={this.state.currPage} setCurrPage={this.setCurrPage} answers={this.state.answers} updatePage={this.updatePage} updateQuest={this.updateQuest} searchTag = {this.searchTag} isTag={this.isTag} stripTag = {this.stripTag} quest={this.state.all_quest} userType= {this.state.userType} updateUserType={this.updateUserType}/>
             {/* If the currPage is welcom page */}
             {(this.state.currPage === "welcome") && (
            <Welcome currPage={this.state.currPage} setCurrPage={this.setCurrPage} updateUserType={this.updateUserType}/>
            )}
            {/* If the currPage is register page */}
            {this.state.currPage === "register" && (
            <Register currPage={this.state.currPage} setCurrPage={this.setCurrPage}/>
            )}
            {(this.state.currPage === "login") && (
             <Login currPage={this.state.currPage} setCurrPage={this.setCurrPage} userSession={this.state.userSession} updateUserSession={this.updateUserSession} updateUser={this.updateUser} updateUserType={this.updateUserType}/>
            )} 
            {((this.state.currPage === "all_question") || (this.state.currPage === "tags_page") || (this.state.currPage === "answer_page") || (this.state.currPage === "question_form") || (this.state.currPage === "answer_form") || (this.state.currPage === "filterTags") || (this.state.currPage === "search") || (this.state.currPage === "all_question_link")) && (
            <MainTable currPage={this.state.currPage} setCurrPage={this.setCurrPage} updatePage={this.updatePage} updateQuest={this.updateQuest} quest={this.state.quest} answers={this.state.answers} updateAnswers={this.updateAnswers} updateTags={this.updateTags} searchTag = {this.searchTag} isTag={this.isTag} stripTag = {this.stripTag} userType={this.state.userType} user={this.state.user} updateUserType={this.updateUserType}/>
            )}
            </div>
        );
    } 
}

function compareDates(x, y){
  return new Date(y.ask_date_time) - new Date(x.ask_date_time);
}

export default MainPage;
